@section('js')
    <script>
        $(document).on('click', '.minus_counter', function(e) {
            e.preventDefault();
            var qtyProduct = $(this).parent().find('.qtyProduct');
            let value = +qtyProduct.val();
            let max = +qtyProduct.attr('max');
            let min = +qtyProduct.attr('min');

            if (value > min) {
                value = value - 1;
                qtyProduct.val(value);
                $(this).parent().find('.action-number').html(value);
            }
            $.ajax({
                url: "{{ route('front.cart.update') }}",
                type: "POST",
                dataType: "html",
                data: {
                    _token: "{{ csrf_token() }}",
                    id: $(this).data('id'),
                    qty: value
                },
                success: function(respons) {
                    var res = JSON.parse(respons);
                    if (res.status === true) {
                        $('#carts').html(res.data.carts);
                        $('#totals').html(res.data.totals);
                    }
                    // $('#carts').html(response);
                    // $('#totals').html(response);
                }
            })
        });

        $(document).on('click', '.plus_counter', function(e) {
            // e.preventDefault();
            var qtyProduct = $(this).parent().find('.qtyProduct');
            let value = +qtyProduct.val();

            if (value) {
                value = value + 1;
                qtyProduct.val(value);
                $(this).parent().find('.action-number').html(value);
            }

            $.ajax({
                url: "{{ route('front.cart.update') }}",
                type: "POST",
                dataType: "html",
                data: {
                    _token: "{{ csrf_token() }}",
                    id: $(this).data('id'),
                    qty: value
                },
                success: function(respons) {
                    var res = JSON.parse(respons);
                    if (res.status === true) {
                        $('#carts').html(res.data.carts);
                        $('#totals').html(res.data.totals);
                    }
                }
            })
        });
    </script>
